package EjerciciosParte1;

public class EjercicioArrays02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int [] num= new int[20];
		for (int i=0;i<20;i++) {
			num[i]=((int)(Math.random()*(99-0)));
			System.out.println("Elemento nº "+(i+1)+" ---> "+num[i]);
		}
		
	}
	}


